void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.1 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    float offset = 0.0f;
    float freq = uv.y * 2.0f + sin(iTime * 2.0);
    offset += sin(freq * 10.0);
    offset += sin(freq * 10.0 * 0.5) * 0.5;
    offset += sin(freq * 10.0 * 0.5 * 0.5 * 0.5) * 0.5 * 0.5 * 0.5;
    offset *= 0.05;

    float v = pow( 1.0 - abs( uv.x - 0.5 + offset), 10.0);

    col.r = 0.0;
    col.g = v;
    col.b = 0.0;



    // Output to screen
    fragColor = vec4(col,1.0);
}
