// xor encryption/hash variant/fork of shader "Inverse Hash" by PrzemyslawZaworski @ https://www.shadertoy.com/view/Dts3z8
// using inherent properties of xor, time and some bluenoise

uint RGBAToUint(vec4 rgba) // encode normalized RGBA in range [0..1] to UInt32
{ 
    uint w = uint(rgba.w * 255.0) << 24u; 
    uint z = uint(rgba.z * 255.0) << 16u;
    uint y = uint(rgba.y * 255.0) << 8u;  
    uint x = uint(rgba.x * 255.0);
    return w + z + y + x;
}

vec4 UintToRGBA(uint u)  // decode UInt32 to normalized RGBA in range [0..1]
{
    float w = float((u & 0xff000000u) >> 24) / 255.0;
    float z = float((u & 0x00ff0000u) >> 16) / 255.0;
    float y = float((u & 0x0000ff00u) >>  8) / 255.0;
    float x = float((u & 0x000000ffu)      ) / 255.0;
    return vec4(x, y, z, w);
}

uint Hash(vec2 uv, uint x) 
{
    uint bn = RGBAToUint(textureLod(iChannel2, (uv * iResolution.xy + iTime) / 1024.0f, 0.0f));
    x = x ^ uint(iFrame) ^ bn;
    return x;
}
// unhash = hash //
uint Unhash(vec2 uv, uint x) 
{
    uint bn = RGBAToUint(textureLod(iChannel2, (uv * iResolution.xy + iTime) / 1024.0f, 0.0f));
    x = x ^ uint(iFrame) ^ bn;
    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * vec2(3.0, 2.0);
    int tile = int(floor(uv.x) + 3.0 * floor(uv.y));
    vec4 color = vec4(0.0);
    if (tile == 0) color = texture(iChannel0, uv);
    if (tile == 1) color = UintToRGBA(Hash(uv, RGBAToUint(texture(iChannel0, uv))));
    if (tile == 2) color = UintToRGBA(Unhash(uv, Hash(uv, RGBAToUint(texture(iChannel0, uv)))));    
    if (tile == 3) color = texture(iChannel1, uv);
    if (tile == 4) color = UintToRGBA(Hash(uv, RGBAToUint(texture(iChannel1, uv))));
    if (tile == 5) color = UintToRGBA(Unhash(uv, Hash(uv, RGBAToUint(texture(iChannel1, uv)))));
    fragColor = color;
}
