void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    vec4 data = texture(iChannel0,fragCoord/iResolution.xy);
    fragColor.xyz = vec3(.5+data.z);

}

#define EDGE false
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 inColor = texture(iChannel0,fragCoord/iResolution.xy);
    vec4 inColor0;
    if(EDGE || fragCoord.x<iResolution.x-1.){
        inColor0= texture(iChannel0,(fragCoord+vec2(1.,0.))/iResolution.xy);
    }else{
        inColor0= vec4(inColor.x-inColor.y,inColor.y,0.,0.);
    }
    vec4 inColor1;
    if(EDGE || fragCoord.x>=1.){
        inColor1= texture(iChannel0,(fragCoord+vec2(-1.,0.))/iResolution.xy);
    }else{
        inColor1= vec4(inColor.x-inColor.y,inColor.y,0.,0.);
    }
    vec4 inColor2;
    if(EDGE || fragCoord.y<iResolution.y-1.){
        inColor2= texture(iChannel0,(fragCoord+vec2(0.,1.))/iResolution.xy);
    }else{
        inColor2= vec4(inColor.x-inColor.y,inColor.y,0.,0.);
    }
    vec4 inColor3;
    if(EDGE || fragCoord.y>=1.){
        inColor3= texture(iChannel0,(fragCoord+vec2(0.,-1.))/iResolution.xy);
    }else{
        inColor3= vec4(inColor.x-inColor.y,inColor.y,0.,0.);
    }
    inColor.z=((inColor0.x+inColor1.x+inColor2.x+inColor3.x)/4.-inColor.x)*1.5;
    inColor.y+=inColor.z;
    inColor.x+=inColor.y;
    if(iMouse.z>0.){
        if(length(fragCoord-iMouse.xy)<1.){
            inColor.x=sin(iTime*20.)*50.;
        }
    }
    fragColor.xyz=inColor.xyz;
}
