void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 6.*( u+u - R ) / R.y;

    O = vec4(sqrt( .5 + .5 * mod(ceil(U.x),2.)                // backgroud + columns
              * sin( 12.6*(  U.x*(mod(ceil(U.x/2.),2.)*2.-1.) // bricks direction
                           + ceil(2.*U.y)/8.))                // bricks offset
            ));
}
