#define PI 3.1415926535

//uniform float iTime;
//uniform float iResolution;
uniform float iAmplitude;
uniform float iFrequency[3];
uniform sampler2D screenTexture;


vec2 tile(vec2 uv, float close_up){
    uv *= close_up;
    return fract(uv);
}

float rect(in vec2 st, in vec2 size){
	size = 0.25 - size * 0.25;
    vec2 uv = smoothstep(size, size + size * vec2(0.002), st*(1.0-st));
	return uv.x * uv.y;
}

float circle(vec2 uv, float radius){
    vec2 pos = vec2(0.5) - uv;
    radius *= 0.75;
    return 1.0 - smoothstep( radius - (radius*0.05), radius + (radius*0.05), dot(pos,pos) * PI);
}


float circle_draw(vec2 uv, float radius) {
    return  circle(uv + vec2(0.0, -0.5), radius)+
            circle(uv + vec2(0.0, 0.5), radius)+
            circle(uv + vec2(-0.5, 0.0), radius)+
            circle(uv + vec2(0.5, 0.0), radius);
}

float sampleMusic()
{
    return texture(iChannel0, vec2(0.25,0.0)).x * 0.02;
}   

void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    uv.x *= aspect;

    vec3 color = vec3(0.0);

    float moving_radius_01 = 0.2;
    float moving_speed_01 = 0.5;
    vec2 grid_01 = tile(uv + vec2(pow(cos(iTime * moving_speed_01), 3.0),pow(sin(iTime * moving_speed_01), 3.0)) * moving_radius_01, 2.0);

    color += mix(vec3(0.1, 0.0, 0.1), vec3(0.85, 1.0, 0.85),
                rect(grid_01, vec2(0.2))- rect(grid_01, vec2(0.05)));

    float moving_radius_02 = 0.2;
    float moving_speed_02 = 2.0;
    vec2 grid_02 = tile(uv + vec2(-pow(cos(iTime * moving_speed_02), 1.0), pow(sin(iTime * moving_speed_02), 1.0)) * moving_radius_02, 2.0);

    color = mix(color, vec3(0.75, 0.0, 0.2), circle_draw(grid_02, 0.7) - circle_draw(grid_02, 0.2));
    color = mix(color, vec3(0.5, 0.0, 1.0), circle_draw(grid_02, 0.05));

    fragColor = vec4(vec3(color), 1.0);

}
