void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#define PI 3.141592653589
#define RADIUS 0.6
#define TUBERADIUS 0.2

vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

mat3 rotx = mat3(vec3(1,0,0), vec3(0,cos(iTime),-sin(iTime)), vec3(0,sin(iTime),cos(iTime)));
mat3 roty = mat3(vec3(cos(iTime),0,sin(iTime)), vec3(0,1,0), vec3(-sin(iTime),0,cos(iTime)));

vec3 pos = vec3(RADIUS + TUBERADIUS * cos(PI * p.x), TUBERADIUS * sin(PI * p.x), p.y);
pos = rotx * pos;
pos = roty * pos;

vec3 color = vec3(0.0);

float d = length(vec2(length(pos.xz) - RADIUS, pos.y)) - TUBERADIUS;

if (d < 0.0) {
    vec3 n = normalize(vec3(pos.xz, d));
    vec3 l = normalize(vec3(1.0, 1.0, 1.0));
    float diffuse = max(dot(n, l), 0.0);
    color = vec3(1.0, 0.5, 0.0) * diffuse;
}

fragColor = vec4(color, 1.0);

}
