#define r iResolution

vec2 m(vec3 p) {
    vec3 mp=p;
    p =mod(p,4.);

    float d = distance(p, vec3(2.)) - 1.;
    d=min(d,distance(p.xz,vec2(2.0))-0.4);
    d=min(d,distance(p.xy,vec2(2.0))-0.4);
    d=min(d,distance(p.yz,vec2(2.0))-0.4);


    mp+=normalize(mp)*iTime*1.2;
    mp *=3.;
    float a = sin(mp.x)*sin(mp.y)*sin(mp.z);
    return vec2(d+a*0.2,d+.6);
}

void mainImage( out vec4 o, in vec2 f )
{
    f = (f.xy / r.xy * 2. -1.);
    f.x*=r.x/r.y;
    float c=cos(iTime*0.2);
    float s=sin(iTime*0.2);
    vec3 e=vec3(sin(iTime*0.4)*12.,c*12.,iTime);
    mat3 ry=mat3(vec3(s,0.,c),vec3(0,1,0),vec3(c,0.,-s));
    mat3 rx=mat3(vec3(1,0,0),vec3(0,-s,c),vec3(0,c,s));
    vec3 v=normalize(ry*rx*vec3(f.x, f.y, -1));
    vec3 d;
    for (float i = 0.; i < 100.; i+=1.)
    {
        vec2 r = m(e);
        d.z = r.y;
        if(r.x < .01 || d.y > 100.) break;
        e  += r.x*v*0.13;
        d.y += r.x;
        d.x += 0.01;
    }

    o = vec4((1.-d.z*1.5)+ 1.-d.y/50.+d.x*v.rgrr);

}
