// From Iq https://www.shadertoy.com/view/llGSzw
vec3 hash3( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uvec3 k = n * uvec3(n,n*16807U,n*48271U);
    return vec3( k & uvec3(0x7fffffffU))/float(0x7fffffff);
}

// Heavely inspired by "fractal 77 gaz" -> https://www.shadertoy.com/user/gaz/
mat2 rot(float v) {
    float a = cos(v);
    float b = sin(v);
    return mat2(a,b,-b,a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 rd = normalize(vec3(uv*2.-1., 2.));
    float PI =3.14156 ;
    if (iMouse.z>0.5) {
        vec2 muv = iMouse.xy/iResolution.xy-0.5;
        rd.xz = rot(muv.x*PI*2.) * rd.xz;
        rd.yz = rot(muv.y*PI*2.) * rd.yz;
    }

    vec3 ro = vec3(0.,0.,-.5);
    float t = 0.;

    vec3 seed = hash3( uint(iTime/10.) + 3u );
    vec3 seed2 = hash3( uint(iTime/10.) );
    float time = mod(iTime, 10.);
    float fade = smoothstep(0.,1., time) * smoothstep(10.,9.5, time);


    vec3 col = vec3(0.);
    for(int i=0; i<80; i++) {
        vec3 p = ro + rd * t;
        p.xz = rot(iTime*0.02+0.3) * p.xz;
        p.xy = rot(-iTime*0.03-.2) * p.xy;

      //  float s =2.  , r2 ;
        float s = 2.*(1.+0.*sin(iTime*0.2)  ), r2;

        for(int j=0; j<8; j++) {
            r2 = (/*fade**/1.7)/min(dot(p,p),1.6*(1.+0.2*sin(iTime*0.2 )  ));
        //  p = abs(p*r2) - (seed+.1)*5.;      // case default
         //   p = abs(p*r2) - (seed+.1)*5.*0.5;  // case a
          p = abs(p*r2) - vec3(0.1,10.,2. ); // case b

      //      p =p.x<p.y? p.zxy:p.zyx ;     //8/4 ad  77 gaz style

            s *= r2;
        }

          float d = length(p.xy-0.5)/s;      // (p.xy-0.5*0.)  case default
      //  float d = length(cross(p,vec3(0.577 ) ))/s-2e-4 ; // case aa

        t += d*.5;
        col += vec3(cos(vec3(1.*3.,2.,3.*4.)-iTime*1.5*(seed2.r*2.-1.*0.0) + log(s))*.5+.5) * 0.03 * exp(-.5*d*float(i*i));
    }

    fragColor = vec4(col* fade,1.0);
}
