float circle(vec2 positionScreen)
{
    float c = length(positionScreen);
    return c;

}
vec3 circleBright(float size, vec3 color, float c)
{
    vec3 colorC = mix(vec3(1.), color, smoothstep(0.000, size, c));
    return colorC;
}

vec3 drawPlanet(vec2 positionCircle, vec3 backgroundColor, vec3 circleColor, float size)
{
    float c = circle(positionCircle);
    vec3 circleB = circleBright(size, circleColor, c);
    c = 1.-step(size, c);
    return mix(backgroundColor, circleB, c);
}

vec2 rotation(vec2 position, float velocityX, float velocityY)
{
    return(vec2(cos(iTime/velocityX)-position.x, sin(iTime/velocityY)-position.y));
}

