vec3 drawSun(vec2 positionSun, vec3 background, float sizeSun)
{
    float sun = circle(positionSun);
    vec3 sunColor = mix(vec3(1.000,0.464,0.131), vec3(3.949,4.000,1.028), smoothstep(-0.096, 4.746 + cos(iTime), sun));
    sun = sizeSun-sun;
    return mix(background, sunColor, sun);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.y *= iResolution.y/iResolution.x;
    st *=3.;
    st-=0.844;

    vec2 centerScreen = st-vec2(0.5);
    vec3 colorA = vec3(0.175,0.002,0.042);
	vec3 colorB = vec3(0.210,0.108,0.995);
	vec3 color = mix(colorA, colorB, smoothstep(-0.960, 4.648,st.x+-1.144));

	color = backgroundStars(centerScreen, 0.4, color);
    color = drawPlanet(centerScreen+rotation(vec2(0.390,-.540), 2.056, 3.440), color, vec3(0.995,0.036,0.189), 0.264);
    color = drawPlanet(centerScreen+rotation(vec2(0.190,0.180), 3., 3.968), color, vec3(0.799,0.835,0.267), 0.132);
    color = drawPlanet(centerScreen+rotation(vec2(-0.220,-0.310), 2., 3.240), color, vec3(0.160,0.233,0.835), 0.052);
    color = drawSun((centerScreen+vec2(-1.850,-0.890)), color, 1.7);

    fragColor = vec4(color,1.0);
}
