#ifdef GL_ES
precision mediump float;
#endif
#define TAU 6.283185307179586476925286766559


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    float lines = 6.;
    float interval = 0.072;
    float height = 4.340;
    float offset = sin((st.y+iTime*interval)*14.648)*0.1;

    vec3 factor = vec3( log2(cos((st.x+offset)*lines*TAU)*height) );
    //color = vec3(st.x,st.y,1);
    vec3 color1 = vec3(0.);

    vec3 color2 = mix(vec3(1.000,0.891,0.268),vec3(1.000,0.254,0.032),st.y);



	vec3 color_f = mix(color1,color2,factor);
    fragColor = vec4(color_f,1.0);
}
