float thicknessAnim( float t ) 
{
    return t == 0.025 || t < 0.0
    ? t
    : t > 0.5
      ? 0.025 - t * 0.015
      : 0.025;
}

float radiusAnim( float t)
{
    return t == 0.0 || t == 1.0
    ? t
    : t < 0.5
      ? +0.5 * pow(2.0, (20.0 * t) - 10.0)
      : -0.5 * pow(1.1, 10.0 - (t * 20.0)) + 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Variables
    float radius;
    float thickness;
    vec3 innerColor = vec3(1.0);
    vec3 outerColor = vec3(0.06, 0.58, 0.95);
    vec3 bgColor = vec3(0.0, 0.1, 0.3);
    vec3 bloomColor = mix(vec3(0.0, 0.2, 0.4), bgColor, (clamp(iTime - 1.1, 0.0, 1.0)));

    //Animation
    radius = radiusAnim(iTime);  
    thickness = thicknessAnim(iTime);

    //UV
    vec2 uv = (2.0 * fragCoord-iResolution.xy)/iResolution.y;

    //Sheikah symbol
    vec4 symbol = texture(iChannel0, uv + 0.5);


    //Ring setup 
    float sdf = length(uv);
    float radiusSubtract = sdf - radius;
    float ring = abs(radiusSubtract);
    float thicknessSubtract = ring - thickness;
    float sharpEdge = smoothstep(0.0, 0.005, thicknessSubtract);
    float invert = 1.0 - sharpEdge;

    //Color setup
    vec3 ringColor = mix(outerColor, innerColor, invert);
    vec3 bloom = mix(bloomColor, bgColor, ring);

    //Final Image
    vec3 image = mix(bloom, ringColor, invert);
    image = mix(image, symbol.xyz, symbol.a);
    // Output to screen
    fragColor = vec4(image, 1.0);
}
