void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    float g_top = smoothstep(0.6, 1.0, uv.y);
    float g_bot = smoothstep(0.4, 0.0, uv.y);
    vec4 h_top = (mod((uv.x + iTime/10.0) * 100.0, 5.0) <= 0.1)? vec4(col,1.0) : vec4(0.0,0.0,0.0,1.0);
    vec4 h_bot = (mod((uv.x - iTime/10.0) * 100.0, 5.0) <= 0.1)? vec4(col,1.0) : vec4(0.0,0.0,0.0,1.0);
    vec4 v_top = (mod((uv.y + uv.x/10.0) * 100.0, 5.0) <= 0.1)? vec4(col,1.0) : vec4(0.0,0.0,0.0,1.0);
    vec4 v_bot = (mod((uv.y + uv.x/10.0)* 100.0, 5.0) <= 0.1)? vec4(col,1.0) : vec4(0.0,0.0,0.0,1.0);
    fragColor = g_top * (h_top + v_top) + g_bot * (h_bot + v_bot);
}
