renderpass.code:
#define S(v) smoothstep(13./R.y,0.,v )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, V,
         U = 9.* ( u - .5*R ) / R.y;
    vec3 H =  abs( fract( U * mat3x2(1,0,.5,.87,.5,-.87) ) -.5 );

    float a = .5 - max( H.z ,max(H.x,H.y)) , r = .12;
    O =  vec4(1. - .6* S(a-.04) );           // hex grid

    V =  mod( U+ vec2(.5,0), vec2(2,1.15) ) - vec2(.5,.56); // nodes
    if ( V.x>.5 == U.x>.5 ) r *= .5;         // choose dot size
    if ( V.x>.5 ) V.x--;                     // mid-segment
    a = length(V) - r;
    O = mix(O, vec4(S(abs(a))), S(a) );      // draw dots
}
