//started with this shader: https://www.shadertoy.com/view/ml23WR
//playing around and ended up with something that looks like
//fiber optics cables.

mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

float v1a=0.02;
float v1b=.6;
float v1c=0.01;
float v1d=0.2;
float v1e=5.1;
float v1f=1.0;

float v2a=0.056;
float v2b=.45;
float v2c=.1;
float v2d=0.35;
float v2e=3.1;
float v2f=2.5;

float va=v2a;
float vb=v2b;
float vc=v2c;
float vd=v2d;
float ve=v2e;
float vf=v2f;

float t = iTime+5.0;

if( t > 65.0 ) {
    va=v1a;
    vb=v1b;
    vc=v1c;
    vd=v1d;
    ve=v1e;
    vf=v1f;
}

vec3 P,Q,o;
P.x=t*va;
float i,d=1.0;
for(;i++<2e2 && d>1e-4;

P+=normalize(vec3((fragCoord.xy*0.2-iResolution.xy)/iResolution.y,2))*d)
{
    Q=P;
    Q.xy=fract(Q.xy)-vb;
    for(int j;j++<8;Q+=Q)
      Q.xy=abs(Q.xy*rotate2D(Q.z+t*vc))-.15;
    d=(length(Q.xy)-vd)/1e3;

}
o+=ve/i;
o.x += o.x+o.y+o.z*vf;



 fragColor =vec4(o,1);
}
