// Directed Photon Mapping V2 by gcgac, with progressive rendering and mouse controls
// Original code in Buffer A.
// 
// Original by https://www.shadertoy.com/user/gcgac @ https://www.shadertoy.com/view/clsXzB

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

//Thanks to Inigo Quilez for the PRNG and trigophers for helping me make this progressive (in progress)


int seed = 2;
vec3 rgb;
int frand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float rand(void) { return float(frand())/32767.0; }
vec3 point;
vec3 lightpos = vec3(0.0,90,70.0);
float hash1( uint n ) {
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uint(0x7fffffffU))/float(0x7fffffff);
}


float sde;
vec3 pos;
float maxl = 10000.0;


vec3 sphrand(){
    point = vec3(2.0);
    while(length(point)>=1.0){
        point = vec3(rand()*2.0-1.0,rand()*2.0-1.0,rand()*2.0-1.0);
    }
    return point;
}

float plane_sde(vec3 ray, vec3 nor, float planey, vec3 col){
    if (dot(ray,nor)+planey < sde){
        sde = dot(ray,nor)+planey;
        rgb = col;
    }
    return dot(ray,nor)+planey;
}


float sphere_sde(vec3 ray, vec3 position, float radius, vec3 col){
    if (length(position-ray)-radius < sde){
        sde = length(position-ray)-radius;
        rgb = col;
    }
    return length(position-ray)-radius;
}


vec4 map(vec4 dstLo, vec4 dstHi, vec2 srcLo, vec2 srcHi, vec2 x){
    return dstLo + (dstHi-dstLo)*((x-srcLo)/(srcHi-srcLo)).xxyx;
}
float calc_sdf(vec3 ray){
    sde = maxl;
    vec2 mouseInput = dot(iMouse.zw,iMouse.zw) < 10.0 ? vec2(0.25,0.1875) : abs(iMouse.zw)/iResolution.xy;
    vec4 sphL = map(vec4(-20,11,80-40,20),vec4(-10, 2,80+40,10),vec2(0.25,0.0625),vec2(0.75,0.3125),mouseInput);
    vec4 sphR = map(vec4( 10, 2,80-40,10),vec4( 20,11,80+40,20),vec2(0.25,0.0625),vec2(0.75,0.3125),mouseInput);

    sphere_sde(ray,sphL.xyz,sphL.w, COLOR_SPHERE_L);
    plane_sde(ray,vec3( 0,1,0),10.0, vec3(1,1,1));
    plane_sde(ray,vec3( 1,0,0),21.0, vec3(1,1,1));
    plane_sde(ray,vec3(-1,0,0),21.0, vec3(1,1,1));
    sphere_sde(ray,sphR.xyz,sphR.w, COLOR_SPHERE_R);
    return sde;
}


vec4 raycol(vec3 orig, vec3 dir, float ml){
    float len = 0.0;
    sde = ml;
    float steps = 0.0;
    pos = orig;

    while (len < ml && sde > 0.1){
        sde = calc_sdf(pos);
        pos += dir*sde;
        len += sde;
        steps += 1.0;
    }
    return vec4(pos,steps);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 newfrag = vec2(fragCoord.x+iResolution.x/2.0,fragCoord.y+iResolution.y/2.0); 
    uvec2 p = uvec2((newfrag-.5*iResolution.xy)/iResolution.y*1000.0);
    seed = int(round(hash1( p.x + 1920U*p.y + (1920U*1080U))*100.0));
    seed += iFrame;
    vec3 pos = vec3(0.0,10.0,10.0);
    vec3 direction = normalize(vec3(uv.x,uv.y,1));
    vec4 o;
    float steps;
    o = raycol(pos,direction,500.0);
    if (sde <= 0.1){
        steps =o.w;
        vec3 inter = o.xyz;
        vec3 colour = rgb;
        for (int i = 0; i<100; i++){
            vec3 offset = sphrand();
            offset *= COC;
            vec3 hit = raycol(lightpos,normalize(inter+offset-lightpos),500.0).xyz;
            if (length(hit-inter)<COC){
                colour += rgb*(1.0-length(hit-inter)/COC);
            }
        }
        colour /= 50.0;
        fragColor = vec4(colour,1.0);
    } else {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 bufA = texelFetch(iChannel0, ivec2(fragCoord), 0);
    vec4 bufB = texelFetch(iChannel1, ivec2(fragCoord), 0);
    fragColor = iTime*iFrameRate <= 2.5 ? bufA : mix(bufA,bufB,KEEP_PREV_BUFFER);
}

#define KEEP_PREV_BUFFER 0.9375

//#define COC 8.0
#define COC 16.0

//#define COLOR_SPHERE_L vec3(1,0,0)
//#define COLOR_SPHERE_R vec3(0,1,1)
#define COLOR_SPHERE_L vec3(1,0.5,0)
#define COLOR_SPHERE_R vec3(0,0.5,1)
