// Main shader function
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate the UV coordinates of the current pixel
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Calculate the time elapsed in seconds
    float time = iTime * 0.5;

    // Calculate the distance from the center of the screen
    float dist = length(uv) * 10.0;

    // Generate the noise for the star grid
    float stars = fract(dist + time);

    // Set the output color for the current pixel
    fragColor = vec4(stars, stars, stars, 1);
}
