float random2d(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float randomRange (in vec2 seed, in float min, in float max) {
		return min + random2d(seed) * (max - min);
}

float insideRange(float v, float bottom, float top) {
   return step(bottom, v) - step(top, v);
}

float AMT = 0.2;
float SPEED = 32.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float time = floor(iTime * SPEED);    
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 outCol = texture(iChannel0, uv).rgb;

    float maxOffset = AMT/32.0;
    for (float i = 0.0; i < 10.0 * AMT; i += 0.25) {
        float sliceY = random2d(vec2(time , 2345.0 + float(i)));
        float sliceH = random2d(vec2(time , 925.0 + float(i))) * 0.075;
        float hOffset = randomRange(vec2(time , 25.0 + float(i)), -maxOffset, maxOffset);
        vec2 uvOff = uv;
        uvOff.x += hOffset;
        if (insideRange(uv.y, sliceY, fract(sliceY+sliceH)) == 1.0 ){
        	outCol = texture(iChannel0, uvOff).rgb;
        }
    }

    float maxColOffset = AMT/0.0;
    float rnd = random2d(vec2(time , 45.0));
    vec2 colOffset = vec2(randomRange(vec2(time , 45.0),-maxColOffset,maxColOffset), 
    randomRange(vec2(time , 725.0),-maxColOffset,maxColOffset));

	fragColor = vec4(outCol,1.0);
}
