// Current version info:

// Tried my hand at implementing the Runge Kutta 4 method for integrating the differential equation.
// The new code for the implementation is in buffer A.
// Not sure if this works as well for a stochastic derivative estimate, such as this,
// but it seems to work a bit better. I also modified the sample and step parameters a bit in the Common tab,
// since the RK4 method is more expensive, but seems to be more accurate.
// Still seeing the artifact where the entire target shape is not completely filled. Any ideas welcome :)

// Previous versions:

// - Added more target shapes. Change the SCENE macro paramter in the "Common tab"

// Original info:

// Based on the paper Iterative :alpha: (De)Blending: https://ggx-research.github.io/publication/2023/05/10/publication-iadb.html
// and
// https://www.shadertoy.com/view/mlXyRf by Fabrice Neyret

// Buffer A computes the Transport Map by iterative alpha (de-)blending.
// Buffer B draws the trails / trace
// Buffer C draws the particles. Only a few while the mapping is computed, but then shows the rest.
// The Common tab has additional parameters and helper functions.

// If you have a faster GPU you can increase the 'num_substeps' parameter in the Buffer A tab, or reduce if you are on a slower GPU
// Alternatively to can reduce or increase the 'total_steps'.
// Lower number of total iteration steps takes less time but is more noisy
// Higher number of total iteration steps takes longer time but is less noisy

vec4 draw_target_shape(vec4 fragColor, vec2 fragCoord)
{
    vec2 uv = pixel_to_uv(fragCoord, iResolution.xy);
#if SCENE == 4
    float sdf1 = circle_sdf(uv, circle_center1, circle_radius1);
    float sdf2 = circle_sdf(uv, circle_center1, circle_radius1*.5);
    float sdf3 = circle_sdf(uv, circle_center2, circle_radius2);
    float sdf4 = circle_sdf(uv, circle_center2, circle_radius2*.5);
    float alpha1 = min(sdf1, sdf3);
    float alpha2 = min(sdf2, sdf4);
    alpha1 = smoothstep( 2./iResolution.y, 0., abs(alpha1) );
    alpha2 = smoothstep( 2./iResolution.y, 0., abs(alpha2) );
    float alpha = max(alpha1, alpha2);
    return mix(fragColor, vec4(1), alpha);
#else
    float sdf1 = circle_sdf(uv, circle_center1, circle_radius1);
    float sdf2 = circle_sdf(uv, circle_center2, circle_radius2);
    float alpha1 = abs(sdf1);
    float alpha2 = abs(sdf2);
    float alpha = min(alpha1, alpha2);
#endif
    return mix(fragColor, vec4(1), smoothstep( 2./iResolution.y, 0., alpha ) );
}

vec4 draw_source_shape(vec4 fragColor, vec2 fragCoord)
{
   vec2 uv = pixel_to_uv(fragCoord, iResolution.xy);
   float sdf = square_sdf(uv, box_center, radius);
   float alpha = abs(sdf);
   return mix(fragColor, vec4(1), smoothstep( 2./iResolution.y, 0., abs(alpha) ) );
}

vec4 get_transport_map(vec2 U) { return  texelFetch( iChannel0, ivec2(U), 0 ); }
vec4 get_trace(vec2 U) { return  texelFetch( iChannel1, ivec2(U), 0 ); }
vec4 get_particles(vec2 U) { return  texelFetch( iChannel2, ivec2(U), 0 ); }

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    fragColor = get_trace(fragCoord);

    // --- draw source & target shapes -------- 
    fragColor = draw_source_shape(fragColor, fragCoord);
    fragColor = draw_target_shape(fragColor, fragCoord);

    // --- plot particles ----------------------------
    vec4 particles = get_particles(fragCoord);

    fragColor = vec4(mix(fragColor.rgb, particles.rgb, particles.a), 1.);

    fragColor = gamma_correct(fragColor.rgb);
}

const float PI = 3.14159265359;

// Solver parameters ( mostly used in buffer A)

// For rejection sampling.
// Increase this if you see stragglers at the end of the simulation, i.e. points outside the target area.
const float max_sampling_iterations = 1000.;

const int num_substeps = 5;      // alpha (de-)blend steps per frame
const float total_steps = 5000.; // total number of steps t between 0 and 1
const float dt = 1./total_steps;  // step increment

// Shape parameters

#define SCENE 4

#if SCENE == 0

// Two discs aside
const float circle_radius1 = .25; 
const float circle_radius2 = .25; 
vec2 circle_center1 = vec2(1.25, .1 );
vec2 circle_center2 = vec2(1.05, .65 );

#elif SCENE == 1

// Two discs colinear
const float circle_radius1 = .25; 
const float circle_radius2 = .25; 
vec2 circle_center1 = vec2(1.3, .55 );
vec2 circle_center2 = vec2(.7, .2 );

#elif SCENE == 2

// Annulus
const float circle_radius1 = .5; 
const float circle_radius2 = .25; 
vec2 circle_center1 = vec2(1., .4 );
vec2 circle_center2 = vec2(1., .4 );

#elif SCENE == 3

// Two discs overlapping
const float circle_radius1 = .45; 
const float circle_radius2 = .45; 
vec2 circle_center1 = vec2(1.25, .1 );
vec2 circle_center2 = vec2(1.05, .5 );

#elif SCENE == 4

// Two annuluses overlapping (annuli? annulodes? :D)
const float circle_radius1 = .35; 
const float circle_radius2 = .35; 
vec2 circle_center1 = vec2(1.275, .05 );
vec2 circle_center2 = vec2(1.00, .5 );

#else

#endif

const float radius = .50;              // disc( (c,c), r ) to square [0,2r]Â²
vec2 box_center = vec2(-.75, -.45 );

// Misc parameters

const int num_particles = 10;

// Gamma / sRGB

const float GAMMA = 2.2;

vec4 gamma_correct( vec3 color )
{
    return vec4(pow(clamp(color, 0., 1.), vec3(1./GAMMA)), 1.);
}

// Noise functions

uint hash(uint x, uint y)
{
    y ^= y >> 13;
    y ^= y << 17;
    y ^= y >> 5;
    y *= 0x2545F491u;

    x ^= y;
    x ^= x >> 13;
    x ^= x << 17;
    x ^= x >> 5;
    x *= 0x4F6CDD1Du;

    return x;
}

float hash(vec2 uv)
{
    uint x = floatBitsToUint(uv.x) | 1u;
    uint y = floatBitsToUint(uv.y);

    y ^= y >> 13;
    y ^= y << 17;
    y ^= y >> 5;
    y *= 0x2545F491u;

    x ^= y;
    x ^= x >> 13;
    x ^= x << 17;
    x ^= x >> 5;
    x *= 0x4F6CDD1Du;

    uint z = ((x>>9) & 0x007FFFFFu) | (0xFF800000u & floatBitsToUint(.5));

    return 2. * uintBitsToFloat(z) - 1.;
}

vec2 hash2(vec2 uv)
{
    float x = hash(uv);
    float y = hash(uv + vec2(x, 0.));
    return vec2(x,y);
}

// SDF functions

float circle_sdf(vec2 p, vec2 circle_center, float circle_radius) { return length(p - circle_center) - circle_radius; }

float square_sdf(vec2 p, vec2 square_center, float box_radius)
{ 
    vec2 f = abs(p-square_center) - box_radius;
    return (f.x >= 0. && f.y >= 0.) ? length(f) : max(f.x, f.y);
}

// Pixel and UV helper functions
vec2 pixel_to_uv(vec2 fragCoord, vec2 iResolution) { return (2. * fragCoord.xy - iResolution.xy) / iResolution.y; }
vec2 uv_to_pixel(vec2 uv, vec2 iResolution) { return 0.5 * (uv.xy * iResolution.y + iResolution.xy); }
vec2 uncentered_to_centered_uv(vec2 uncentered_uv, vec2 iResolution) { return 2.*(uncentered_uv-.5) * vec2(iResolution.x / iResolution.y, 1.); }
vec2 centered_to_uncentered_uv(vec2 centered_uv, vec2 iResolution) { return .5*(centered_uv / vec2(iResolution.x / iResolution.y, 1.)+1.); }

// Distribution functions

// float source_distribution_pdf(vec2 x) { return 1.; } // Unused for now
// float target_distribution_pdf(vec2 x) { return 1.; } // Unused for now

vec2 sample_source_distribution(vec2 hash)
{
    // Shrink a little, this reduces spurious points, at least for me...
    // The only reason I can think of for this problem is because points have to be inside
    // the convex hull / completion of the union of the source and target shapes,
    // otherwise there does not exist a valid convex combination
    // of points from the two shapes that gives the current point as the result.
    // On the boundary of the convex hull, the chance of finding a valid convex combination
    // by rejection sampling is very low...
    hash = (.98*hash+0.01);

    return (2.*radius * hash) + (box_center-radius);
}

vec2 sample_target_distribution(vec2 hash)
{
#if SCENE == 2
    float angle = 2.* PI * hash.y;
    float radius = sqrt(hash.x*(circle_radius1*circle_radius1-circle_radius2*circle_radius2)+circle_radius2*circle_radius2);
    return circle_center1 + radius * sqrt(hash.x) * vec2(cos(angle), sin(angle));   

#elif SCENE == 4

    int circle_idx = int(hash.x > .5);
    vec2 circle_center = circle_idx == 0 ? circle_center1 : circle_center2;
    float circle_radius = circle_idx == 0 ? circle_radius1 : circle_radius2;

    float R1 = circle_radius;
    float R2 = circle_radius * .5;

    float rnd = 2. * hash.x - float(circle_idx == 1); 
    float R = sqrt(rnd*(R1*R1 - R2*R2) + R2*R2);

    float angle = 2.* PI * hash.y;
    return circle_center + R * vec2(cos(angle), sin(angle));

#else
    int circle_idx = int(hash.x > .5);
    vec2 circle_center = circle_idx == 0 ? circle_center1 : circle_center2;
    float circle_radius = circle_idx == 0 ? circle_radius1 : circle_radius2;
    float angle = 2.* PI * hash.y;
    return circle_center + circle_radius * sqrt(hash.x) * vec2(cos(angle), sin(angle));
#endif
}

bool is_inside_source_distribution(vec2 position)
{
    return square_sdf(position, box_center, radius) < 0.;
}

bool is_inside_target_distribution(vec2 position)
{
#if SCENE == 2 
    return 
        circle_sdf(position, circle_center1, circle_radius1) < 0.
        &&
        circle_sdf(position, circle_center2, circle_radius2) > 0.
        ;

#elif SCENE == 4        
       return 
       (
        circle_sdf(position, circle_center1, circle_radius1) < 0.
        &&
        circle_sdf(position, circle_center1, circle_radius1*.5) > 0.
       )
       ||
       (
        circle_sdf(position, circle_center2, circle_radius2) < 0.
        &&
        circle_sdf(position, circle_center2, circle_radius2*.5) > 0.
       )
        ; 
#else
      return 
        circle_sdf(position, circle_center1, circle_radius1) < 0.
        ||
        circle_sdf(position, circle_center2, circle_radius2) < 0.
        ;  
#endif
}

vec2 alpha_blend(vec2 source_point, vec2 target_point, float alpha)
{
    return mix(source_point, target_point, alpha);
}

vec2 alpha_deblend(vec2 blended_point, vec2 sample_point, float alpha)
{
    // Solve 
    //
    //    blended_point = (1.-alpha) * sample_point + alpha * target_point
    //  
    // for 'target_point'

    return ( blended_point - (1.-alpha)*sample_point ) / alpha;
}

// DDIM
// See appendix C of paper

vec2 ddim_alpha_blend(vec2 source_point, vec2 target_point, float alpha)
{
    alpha = clamp(alpha, 0., 1.);
    float beta_0 = sqrt(1.-alpha);
    float beta_1 = sqrt(alpha);
    return beta_0 * source_point + beta_1 * target_point;
}

vec2 ddim_alpha_deblend(vec2 blended_point, vec2 sample_point, float alpha)
{
    alpha = clamp(alpha, 0., 1.);
    float beta_0 = sqrt(1.-alpha);
    float beta_1 = sqrt(alpha);
    return ( blended_point - beta_0*sample_point ) / beta_1;
}

// Transport map buffer

// Each pixel corresponds to a source location
// The value of each pixel gives a target location


vec4 get_transport_map(vec2 U) { return texelFetch( iChannel0, ivec2(U), 0 ); }

vec4 init_transport_map(vec2 fragCoord)
{
    vec2 box_uv = fragCoord.xy/iResolution.xy;
    return vec4(sample_source_distribution(box_uv), 0., 0.);
}

struct rejection_sample_result
{
    vec2 source_point;
    vec2 target_point;
    bool accepted;
};

rejection_sample_result rejection_sample(float t, vec2 current_point, vec2 uv)
{
    vec2 Hash;
    vec2 source_point;
    vec2 target_point;
    bool accepted;

    for( float i = 0.; i < max_sampling_iterations && !accepted; i++)    
    {  
        Hash = hash2( current_point + vec2(t -i/1.3) + uv );

        if ( t < .5 ) // --- if first half of iterations:
        {
            target_point = sample_target_distribution(Hash);              
            source_point = alpha_deblend(current_point, target_point, 1.-t);
            accepted = is_inside_source_distribution(source_point);// reject up to A in square
        }
        else // --- if second half of iterations: 
        {
            source_point = sample_source_distribution(Hash);
            target_point = alpha_deblend(current_point, source_point, t);
            accepted = is_inside_target_distribution(target_point); // reject up to B is disc
        }
    }

    rejection_sample_result result;
    result.source_point = source_point;
    result.target_point = target_point;
    result.accepted = accepted;

    return result;
}

vec2 get_derivative(vec2 source_point, vec2 target_point)
{
    return target_point - source_point;
}

vec2 get_step(vec2 source_point, vec2 target_point, float dt)
{
    return get_derivative(source_point, target_point) * dt;
}


/*
    RK4: f(t,y) = dy/dt

    float k1 = f(t, y);
	float k2 = f(t+h*.5, y + h * k1 * .5);
	float k3 = f(t+h*.5, y + h * k2 * .5);
	float k4 = f(t+h, y + h * k3);
    return y + h * (k1 + 2*(k2 + k3) + k4) / 6.;

    // See https://en.wikipedia.org/wiki/RungeâKutta_methods#The_RungeâKutta_method
*/
vec2 RK4_iteration(float t, vec2 current_point, vec2 uv, out bool accepted)
{
    vec2 source_point;
    vec2 target_point;
    accepted = true;

    rejection_sample_result sampled_result;

    sampled_result = rejection_sample(t, current_point, uv);
    accepted = accepted && sampled_result.accepted;

    vec2 k1 = get_derivative(sampled_result.source_point, sampled_result.target_point);

    sampled_result = rejection_sample(t + dt*.5, current_point + dt * k1 * .5, uv);
    accepted = accepted && sampled_result.accepted;

    vec2 k2 = get_derivative(sampled_result.source_point, sampled_result.target_point);

    sampled_result = rejection_sample(t + dt*.5, current_point + dt * k2 * .5, uv);
    accepted = accepted && sampled_result.accepted;

    vec2 k3 = get_derivative(sampled_result.source_point, sampled_result.target_point);

    sampled_result = rejection_sample(t + dt, current_point + dt * k3, uv);
    accepted = accepted && sampled_result.accepted;

    vec2 k4 = get_derivative(sampled_result.source_point, sampled_result.target_point);

    vec2 result = current_point + dt * (k1 + 2.*(k2 + k3) + k4) / 6.;

    return result;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame==0) 
    {
        fragColor = init_transport_map(fragCoord);
    }
    if(iFrame > 0)
    {
        vec4 transport_map = get_transport_map(fragCoord); // previous state
        fragColor = transport_map;

        // This gives better results than t = (float(iFrame * num_substeps)/total_steps);
        // Because of rejection sampling, the pixels may be slightly out of sync in terms of iterations,
        // We should let the t-value reflect that, so that t = num_steps / total_steps;
        float num_steps = transport_map.z;
        float t = num_steps / total_steps;

        if ( t > 1. ) // end of iterations
            return;                            

        vec2 current_point = fragColor.xy;                          // P: previous pivot
        vec2 next_point = current_point;
        vec2 Hash;
        vec2 source_point;                                          // point in the source shape ( square )
        vec2 target_point;                                          // point in the target shape ( disc )


        for(int substep = 0; substep < num_substeps; substep++)
        {

            bool accepted = false;

            // ------- deblending phase --------

            // rejection sampling

            vec2 uv = fragCoord.xy/iResolution.xy;
            vec2 RK4result = RK4_iteration(t, current_point, uv, accepted);

            // ------- alpha blending phase --------
            if (accepted)
            {
                num_steps++;
                t += dt;
                next_point = RK4result;
            }

            current_point = next_point;
        }
        fragColor.xy = current_point;
        fragColor.z = num_steps;
    }
}

// Trace-lines buffer

vec4 get_transport_map(vec2 U) { return  texelFetch( iChannel0, ivec2(U), 0 ); }
vec4 get_trace(vec2 U) { return  texelFetch( iChannel1, ivec2(U), 0 ); }

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    vec4 trace = get_trace(fragCoord);

    float t = get_transport_map(fragCoord).z / total_steps;

    if(iFrame == 0)
        fragColor = vec4(0,0,0,1);
    else
        fragColor = trace;

    if(t > 1.) return; // Stop tracing when the schedule is complete

    vec2 Id = vec2(0);
    float min_dist_squared = 1e9;

    // Find nearest particle
    int N = num_particles;
    for( int i = 0; i < N*N; i++ )
    {
        vec2 pixel_uv = ( ( .5 + vec2(i%N, i/N) ) / float(N) );
        vec2 particle_position_uv = get_transport_map( pixel_uv * iResolution.xy ).xy;
        vec2 P = uv_to_pixel(particle_position_uv, iResolution.xy) - fragCoord;  // offset to current pixel

        float dist_squared = dot(P,P);
        if(dist_squared < min_dist_squared)
        {
            min_dist_squared = dist_squared;
            Id = pixel_uv;
        }
    }

    float len = sqrt(min_dist_squared);
    vec3 dot_color =  vec3(Id, 1);

    const float trace_strength = 0.01;
    fragColor.rgb = mix(fragColor.rgb, dot_color, trace_strength * smoothstep( 2.5, 2., len ));
}

// Draw particles buffer
// Visualizes the resulting transport map

vec4 get_transport_map(vec2 U) { return  texelFetch( iChannel0, ivec2(U), 0 ); }
vec4 get_particles(vec2 U) { return  texelFetch( iChannel2, ivec2(U), 0 ); }

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    fragColor = vec4(0);

    vec2 Id = vec2(0);
    float min_dist_squared = 1e12;

    float t = get_transport_map(fragCoord).z / total_steps;

    vec2 pixel_uv_offset = vec2(0);

    int N = num_particles; // Should be same as buffer B to have trails match particles

    // When we reach the end we store the previous state and try to fill out the target mapping
    if(t > 1.)
    {
        pixel_uv_offset = .499*(2.*hash2(fragCoord.xy + iTime)-1.);
        N = 5; // Use fewer when filling over time

        vec4 particles = get_particles(fragCoord);
        fragColor = particles;
    }

    // Find nearest particle
    for( int i = 0; i < N*N; i++ )
    {
        vec2 pixel_uv = (( .5 + vec2(i%N, i/N) + pixel_uv_offset )/float(N));
        vec4 transport_map_particle = get_transport_map( pixel_uv * iResolution.xy );
        float particle_t = transport_map_particle.z / total_steps;
        vec2 particle_position_uv = transport_map_particle.xy;
        vec2 P = uv_to_pixel(particle_position_uv, iResolution.xy) - fragCoord;  // offset to current pixel

        float dist_squared = dot(P, P);

        // Keep if nearest so far.
        // Also check whether we are drawing particles in progress (t < 1.),
        // and and whether the particle we intend to draw has reached its final destination (particle_t >= 1.)
        // This is to prevent stragglers from being drawn (i.e. particles that have not reached t == 1. yet)
        if(dist_squared < min_dist_squared && (t < 1. || particle_t >= 1.))
        {
            min_dist_squared = dist_squared;
            Id = pixel_uv;
        }
    }

    if(min_dist_squared < 1e11) // Check whether a valid nearest particle was found.
    {
        float len = sqrt(min_dist_squared);
        vec2 rowcol = (floor(8.*Id));
        float checker = mod(rowcol.x + rowcol.y, 2.);
        vec3(.678, .747, .902);
        vec4 dot_color =  vec4(vec3(Id,0.) * (.75*checker+.25), 1);

        float alpha = smoothstep( 2.5, 2., len );

        fragColor = mix( fragColor, dot_color, alpha); // draw dots 
    }
}
