#define Z normalize

void mainImage( out vec4 O, vec2 U )
{
    vec3 N = iResolution,             // Normal         
         P = vec3(U+U-N.xy, 0) / N.y, // Ray origin -> Light direction
         R = Z(vec3(-P.xy, 1));       // Ray direction

    // Ray-Sphere intersection
    // and Cook Torrance lighting (ripped apart)
    float b = dot(P, R),          
          r = dot(N = Z(P + R*(sqrt(b*b-dot(P,P) + .4)-b)),R), // dot(N,R)
          k = pow(1. - r,  5.),               // Fresnel
          l = max(dot(N, P = Z(1. - P)), 0.), // dot(N,L)
          h = dot(N, Z(P+R)),                 // dot(N,H) 
          d = 1. - .96*h*h;

    O = sqrt(l 
             * (.5+.5*N.rgbr) 
             * (1.4 - k - k/d/d/(1.+9.*r)/(1.+9.*l)));
}
