vec2 fixUV(vec2 uv) {
    return (2.* uv - iResolution.xy)/min(iResolution.x, iResolution.y);
}
float sdf(vec2 p, float r) {
    return length(p) - r;
}
void mainImage(out vec4 fragColor, in vec2 fragCrood) {
    vec2 uv = fixUV(fragCrood);
    float d = sdf(uv, 0.7);
    vec3 color = vec3(1) - sign(d) * vec3(0.2,0.33,0.46);
    color *= 1. - exp(-3.*abs(d));
    color = mix(color, vec3(1.), smoothstep(0.003, 0.,abs(d)));
    color *=0.8+0.2*sin(abs(d)*311.4);
    vec2 mouse = fixUV(iMouse.xy);
    float md = abs(sdf(mouse, 0.7));
    // color += length(uv-mouse)-md;
    color = mix(color, vec3(1.,0,1),smoothstep(0.01,0.,abs(length(uv-mouse)-md)));
    fragColor = vec4(color,1.0);
}
