void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float d = length(uv);

    float r = sin(uv.x * iMouse.x);
    float g = sin(uv.y * iMouse.y);

    if (iMouse.xy==vec2(0)) {
        r = sin(uv.x * 100.);
        g = sin(uv.y * 100.);
    }

    fragColor = vec4(r, g, 0.0, 1.0);
}
