void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate the normalized pixel coordinates
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy ) / iResolution.x;

    vec3 color = vec3( 0.0 );

    vec2 pos = uv;

    float t = iTime * 0.3;

    float rad = length( pos ) * 2.0;
    float ang = atan( pos.y, pos.x );

    float shape = step( 0.0, cos( (ang + t) * 10.0 ) ) * 0.2 + 0.5;

    // add cutouts
    float cutouts = abs( cos( (ang - t) * 3.0 ) ) * 0.2 + 0.2;
    shape = max(shape - cutouts, 0.0);

    color = vec3( 1.0 - step( shape, rad ) );

    fragColor = vec4( color, 1.0 );
}
