void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    float dx = iResolution.x * exp(t);
    float dy = iResolution.y * exp(t);
    float x0 = fragCoord.x / dx * 4.0 - 1.8549853;
    float y0 = fragCoord.y / dy * 3.0;

    float x = 0.0;
    float y = 0.0;
    int iteration = 0;
    int max_iteration = 5000;
    while (x*x + y*y <= 4.0 && iteration < max_iteration) {
        float xtemp = x*x - y*y + x0;
        y = 2.0*x*y + y0;
        x = xtemp;
        iteration = iteration + 1;
    }
    int i = iteration * 255 / max_iteration;
    fragColor = vec4(i, 0, 0, 1.0);
}
