precision highp float;
#define A 0.05
#define I 30
#define S 9
int s[] = int[S](0, 0, 1, 0, 0, 0, 1, 1, 1);

vec3 c(vec2 p) {
    float t = iTime * A, w[20], x = 0.5, h = 0.0;
    for (int i = 0; i < 20; i++) w[i] = .97 + .028 * sin(float(i) + 4.001 + 0.76 * t);
    for (int i = 0; i < I; i++)
        for (int j = 0; j < S; ++j) {
            x = w[j] * p[s[j]] * x * (1.0 - x);
            h += log2(abs(w[j] * p[s[j]] * (1.0 - 2.0 * x)));
        }
    h /= float(I) * float(S);
    vec3 col_shift = vec3(.8, .55, .00330) + t * 0.74;
    vec3 col1 = cos((col_shift + 2.5 * abs(h)) * 3.14159265) * 0.5 + 0.5;
    vec3 col2 = vec3(0.5) + 0.5 * sin(vec3(0.3, 0.33, 0.3) + 2.5 * abs(h) + t * 0.5);
    return (h < 0.0) ? mix(col1, col2, pow(abs(h), .625)) : vec3(0.0);
}

void mainImage(out vec4 f, in vec2 g) {
    f = vec4(c(3.0 + (1.0 * g.xy / iResolution.xy)), 1.0);
}
