// Fork of "Spiral 1 (Horror)" by lsdlive. https://shadertoy.com/view/4tlBWX
// 2023-11-30 20:15:43
#define MOVEPOW 1.0 * 0.01 //new
mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

float de(vec3 p) {

	p.y += cos(iTime*2.) * .2; //new
    p.x += sin(iTime*2.) * .0; //new
	p.xy *= r2d(iTime + p.z);
    //p.y += atan(p.x, p.y) * iMouse.w / 100.;  // idk cool

	vec3 r;
	float d = 0., s = 1.;

	for (int i = 0; i < 3; i++)

		r = max(r = abs(mod(p*s + 1., 2.) - 1.), r.yzx),
		d = max(d, (.9 - min(r.x, min(r.y, r.z))) / s),
		s *= 3.;

	return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
	uv.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3(((-iMouse.x + iResolution.y / (1.07 + MOVEPOW)) * MOVEPOW)+.1*cos(iTime), // x axis
                    ((-iMouse.y + iResolution.x / (3.50 + MOVEPOW)) * MOVEPOW), -iTime), p;  // y axis

	vec3 rd = normalize(vec3(uv, -1));
	p = ro;

	float it = 0.;
	for (float i=0.; i < 1.; i += .01) {
        it = i;
		float d = de(p);
		if (d < .0001) break;
		p += rd * d*.4;
	}
	it /= .4 * sqrt(abs(tan(iTime) + p.x*p.x + p.y*p.y));

	vec3 c = mix(vec3(.0, .0, .0), vec3(0.2, .6, 1.0), it*sin(p.z));

	fragColor = vec4(c, 1.0);
}
