float rectSDF(vec2 st, vec2 s) {
    st = st * 2.0 - 1.0;
    return max(abs(st.x / s.x), abs(st.y / s.y));
}

float fill(float x, float size) {
    return 1.0 - step(size, x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.776, 0.851, 0.878); // TODO set a color

    vec2 S = vec2(0.35, 0.8);
    vec2 SMid = vec2(0.15, 0.8);
    vec2 SB = vec2(0.52, 0.2);

    vec2 offset = vec2(0.2, 0.1);
    vec2 offsetB = vec2(0.25, 0.15);
    vec2 midOffset = vec2(0.07403, 0.0);

    float MX = iMouse.x / iResolution.x;
    float MY = iMouse.y / iResolution.y;

    float rectALeft = fill(rectSDF(uv + offset * vec2(1.0, -1.0), S), 0.8);
    float rectARight = fill(rectSDF(uv - offset, S), 0.8);
    float rectBLeft = fill(rectSDF(uv + offsetB * vec2(1.0, -1.0), S), 0.25);
    float rectBRight = fill(rectSDF(uv - offsetB, S), 0.25);

    vec3 leftAColor = vec3(0.863, 0.906, 0.773);
    vec3 rightAColor = vec3(0.208, 0.318, 0.361);
    vec3 BColor = vec3(0.647, 0.667, 0.678);

    color = mix(color, rectALeft * mix(leftAColor, rightAColor, MX), rectALeft);
    color = mix(color, rectARight * mix(rightAColor, leftAColor, MY), rectARight);

    color = mix(color, rectBLeft * BColor, rectBLeft);
    color = mix(color, rectBRight * BColor, rectBRight);


    fragColor = vec4(color, 1.0);
}
