void mainImage( out vec4 fragCol, in vec2 fragCoord )
{
    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y*2.;
    float t=iTime,
          a=1.95,
          b=sin(t)*3.,
          m=7.,
          n=sin(t/5.)*19.-10.,
          c=(1.-abs(1.-abs
             ( a*cos(uv.x*m)*cos(uv.y*n)
              +b*cos(uv.x*n)*cos(uv.y*m) )));   
    fragCol=vec4(c*sin(t),1.-c,c*cos(t),1.);
}
