void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv, uv0 = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    float a = 0.1*iTime;
    uv0 = uv0.x * vec2(cos(a), sin(a)) + uv0.y * vec2(-sin(a), cos(a));
    uv = uv0;

    vec3 color = vec3(0, 0, 0);
    for (float j=0.; j<3.; j++) {
        uv = fract(1.5*uv) -0.5;
        for (float i=0.; i<3.; i++) {
            float refrac = (i-1.)*length(uv0);
            float d1 = length(uv+ 0.03*refrac);
            float d2 = length(uv0);
            float r = d1*exp(-d2);
            r = sin(8.*r + iTime);
            r = pow(0.04/abs(r), 1.2);
            color[int(i)] += r;
        }
    }


    fragColor = vec4(color, 1.0);
}
