float GetFFT(int tx)
{    
    return texelFetch(iChannel0, ivec2(tx,0), 0 ).x; 
}

float Wave(int tx)
{
    return texelFetch(iChannel0, ivec2(tx,1), 0 ).x ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    int tx = int(uv.x / .01);

    float bass = GetFFT(tx * 2);

    vec3 col = 0.5 + 0.5 * sin(iTime+uv.xyx+vec3(abs(pow(sin(GetFFT(tx)), 1.2) + uv.x),(pow(GetFFT(tx), 1.2) + uv.y),4)) 
     +sin(Wave(tx)) * uv.x;
    fragColor = vec4((col   * (tan(GetFFT(tx)*0.7))) + (uv.y / -abs(GetFFT(tx))),pow(1.0, 1.2));
}
