float sideOfBlob(vec2 uv, float size, float smoothing)
{
    float d = length(uv);


    d = smoothstep(size-smoothing, size+smoothing, d);

    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 um = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 col = vec3(1.0, 1.0, 1.0);

    col *= sideOfBlob(uv, 0.3, 0.2);

    col *= sideOfBlob(uv - um, 0.1, 0.4);

    //col = step(0.5, col);
    col = smoothstep(vec3(0.0), fwidth(col), col - 0.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
