vec3 render(vec2 fragCoord, float time) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 frag;
    if (uv.x<1.0/3.0) {
        frag  = round(fragCoord);
    } else if (uv.x<2.0/3.0) {
        frag = round(fragCoord/(1.0+(7.0*pow(3.0*uv.x-1.0,4.0))*pow(2.0,sin(time/4.0))));
    } else {
        frag = round(fragCoord/(1.0+(7.0*pow(2.0,sin(time/4.0)))));
    }
    //vec2 maus = round(iMouse.xy/);


    float xval;
    if(mod(frag.y,frag.y)==0.0){xval=1.0;}else{xval=0.0;}
    //if(frag.x==maus.x&&frag.y==maus.y){xval=1.0;}
    // Output to screen
    //return vec3(xval,1.0/16.0*(1.0+15.0*pow(3.0*uv.x-1.0,4.0)),xval);
    if (mod(uv.x,0.1)<0.05&&uv.x>0.76) {
        return vec3(0.0,mod(frag.y,0.224927)/0.224927,xval);
    } else {
        return vec3(xval,xval,xval);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 renderAccum = render(fragCoord, iTime);
    float accum = 1.0;
    for (float i = 0.1924927; i <= 1.0; i+=0.1924927) {
        for (float j = 0.1924927; j <= 1.0; j+=0.1924927) {
            accum += 4.0;
            renderAccum += render(fragCoord+vec2(i,j), iTime);
            renderAccum += render(fragCoord+vec2(-i,j), iTime);
            renderAccum += render(fragCoord+vec2(i,-j), iTime);
            renderAccum += render(fragCoord+vec2(-i,-j), iTime);
        }
    }

    fragColor = vec4(renderAccum/accum,1.0);
}
