 * (c) 2023 s.fx
 * 
 * Modified and documented fork of
 * https://www.shadertoy.com/view/4t33z8
 * by thallippoli
 */

const float SCALE      = .1;                 // scale of noise texture
const float STRENGTH   = 1.5;                // amplitude of noise 
const float OFFSET     = .03;                // offset amplitude
const float PI         = 3.1415926535897932; // it's just pi
const bool  BLUE_TINT  = true;               // toggle color grading
const vec3  BLUE       = vec3(.8, .8, 1.5);  // blue color for color grading
const float BLUE_RATIO = .5;                 // mix ratio for color grading
const bool  LIGHT      = true;               // toggle weird noise light effects
const float THRESHOLD  = .8;                 // threshold for dark areas in noise

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv     = fragCoord.xy / iResolution.xy;
    vec2 warpUV = SCALE * uv;

    // just shift the texture
    vec2 st = warpUV + vec2(iTime * .1, iTime * .05);

    // get value from noise (only one int8 channel) using coord and make it brighter
    float warpedCol = texture(iChannel0, st).x * STRENGTH;

    // offset for texture by calculating angle from noise
    vec2 offset = OFFSET * vec2(cos(warpedCol * PI), sin(warpedCol * PI));

    // query texture color at uv using offset vector
    vec3 col = texture(iChannel1, uv + offset).rgb;

    // give water blue tint
    if (BLUE_TINT)
        col = mix(col, col * BLUE, BLUE_RATIO);

    // brighten up brick texture with noise
    if (LIGHT)
        col *= max(warpedCol, THRESHOLD);

    fragColor = vec4(col,  1.);
}
