vec3 palette1(in float t)
{
    vec3 a=vec3(.5,.5,.5);
    vec3 b=vec3(.5,.5,.5);
    vec3 c=vec3(1.,1.,1.);
    vec3 d=vec3(0.,.10,.20);
    return a+b*cos(6.28318*(c*t+d));
}

vec3 palette2(in float t)
{
    vec3 a=vec3(.408,0.,0.);
    vec3 b=vec3(.648,.828,0.);
    vec3 c=vec3(.408,.318,0.);
    vec3 d=vec3(.968,0.,0.);
    return a+b*cos(6.28318*(c*t+d));
}

vec3 palette3(in float t)
{
    vec3 a=vec3(.5,.5,.5);
    vec3 b=vec3(.5,.5,.5);
    vec3 c=vec3(1.,1.,1.);
    vec3 d=vec3(0.,.33,.67);
    return a+b*cos(6.28318*(c*t+d));
}

float PI=3.141592653589793;

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=(fragCoord*2.-iResolution.xy)/iResolution.y;
    uv*=20.;

    float scale=5.;

    vec3 col=vec3(.0);

    float x_mod=mod(uv.x-(iTime*2.),10.)+sin(uv.x)*.5;
    float y_mod=mod(uv.y-(iTime*2.2),10.)-sin(uv.y)*.5;
    if(x_mod<=scale){
        col=vec3(1.);
    }
    else if(y_mod<=scale){
        col=vec3(1.);
    }

    if(x_mod<=scale&&y_mod<=scale){
        col=palette3((uv.x+uv.y)/40.);
    }
    else if(col==vec3(0.)){
        col=(palette1((uv.x+iTime*2.)/60.)+palette1((uv.y+iTime*2.)/60.))/2.;
    }

    if(col==vec3(1.)){
        col=palette2((uv.y-(iTime*2.))/100.)-palette2((uv.x-(iTime*2.))/100.);
    }

    fragColor=vec4(col,1.);
}
