void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time
    float time = iTime;

    // Color cycling
    float colorCycle = sin(time * 0.5) * 0.5 + 0.5;
    vec3 color = vec3(sin(uv.x + colorCycle), cos(uv.y + colorCycle), sin(colorCycle));

    // Noise for stars
    float noise = fract(sin(dot(uv * 100.0, vec2(12.9898, 78.233))) * 43758.5453);

    // Star blinking effect
    float starIntensity = step(0.98, noise) * sin(time * 10.0 * noise);

    // Combine color cycling and stars
    vec3 finalColor = color + vec3(starIntensity);

    // Output to screen
    fragColor = vec4(finalColor, 1.0);
}
