void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalzed pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy/iResolution.xy)-0.5;
    uv*=10.;
    // x**y == y**x
    vec2 iuv = vec2(
        float((uv.x)),
        float((uv.y))
    );
    float n_diff = pow(iuv.x, iuv.y) - pow(iuv.y, iuv.x);

    fragColor = vec4(1.-abs(n_diff));
}
