// https://x.com/YoheiNishitsuji/status/1730507777997410685?s=20

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col; // output color
    float t = iTime*.5; // time

     vec3 q,d=vec3(fragCoord.xy/iResolution.xy+vec2(-.5,.5),1);
     q.zy--;

     float e = 0.;
     float R = 0.;
     for(float i = 0.; i < 70.; i++){
         col += .016 - exp(-e*100.) * .04 * vec4(2.2,vec3(2.5,1.5,0)); // Add to the color

         q += d*e*R*.05; // move the point
         vec3 p = vec3(log2(R=length(q))-t*2.,exp(R-q.z/R),(t*.02)-atan(q.x,q.y)*.5);
         e =-- p.y;
         for(float s=2.; s<50.; s+=s) {
             e += dot(sin(p.xy*s), cos(p.xz*s+t*2.))/s;
         }
      }
      col.r += d.y-.6;
      col.b += d.x-.1;

    // Output to screen
    fragColor = tanh(col)*1.3;
}
