void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 n, q, p = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float d = dot(p, p), S = 12.0, i, a = 0.0;

    for (int j = 4; j < 40; j++)
    {
        mat2 m = mat2(cos(10.0), -sin(10.0), sin(10.0), cos(10.0));
        p *= m;
        n *= m;
        q = p * S + iTime * 4.0 + sin(iTime * 8.0 - d * 2.0) * 0.6 + float(j) + n;
        a += dot(cos(q) / S, vec2(0.2));
        n -= sin(q);
        S *= 1.2;
    }
    fragColor=vec4(0);
    fragColor = (a + 0.2) * vec4(4.0, 1.0, 1.0, 1.0) + a + a - d;
}
