renderpass.code:
mat2 rot(float a)
{
    float c= cos(a);
    float s= sin(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv + vec2(sin(iTime*2.)*0.2, sin(iTime)*0.2);
    uv = uv * 1.5;
    uv = uv*rot(0.5);
    vec3 col = vec3(0.,0.,0.);


    if (uv.x > 0.1 && uv.x < 0.5 && uv.y > 0.1 && uv.y < 0.5)
        col = vec3(61, 137, 156)/255.;
    else
        col = vec3(1.,0.,0.);

    fragColor = vec4(col,1.0);
}
