float rand(vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(12.9898, 78.233))) * 43534.3434);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0);
    vec2 M = vec2(cos(iTime * 0.5), sin(iTime)) * 0.4;

    float n = 10.0;

    uv *= n;
    vec2 pid = floor(uv);
    uv = fract(uv);

    float c = length((uv - M - (pid / n))) * 0.3;

    col = vec3(c, c * (pid.y / n) * 0.5, c * 0.3);

    // Output to screen
    fragColor = vec4(col,1.0);
}
