void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col1 = 0.9 + cos(uv.yyy*34.0+vec3(0,34,15));
    vec3 col2 = 0.3 + 0.3*(uv.yyy + vec3(0,190,190));
    vec4 strips = vec4(col1,1.0);
    if(uv.y > 0.78){
        strips = vec4(col2,1.0);
    }
    if(uv.y < 0.11){
        strips = vec4(col2,1.0);
    }
    fragColor = strips;
}
