vec4 c1 = vec4(0.3, 0.2, 0.7, 1.0);
vec4 c2 = vec4(0.980, 0.965, 0.0882, 1.0);

vec4 sphere(vec3 pos, vec2 frag, float r)
{
    vec2 spVec = pos.xy - frag;
    return mix(c1, c2, clamp(r / length(spVec), 0.0, 1.0)); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/min(iResolution.x, iResolution.y);
    float timedCoeff = mod(iTime / 10.0, 1.0);

    fragColor = sphere(vec3(timedCoeff * 1.4, timedCoeff, 1.0), uv, 0.2);
}
