const vec2 s = vec2(1, 1.7320508);
const float border = .04;
const float speed = 2.;
const float nb = 11.;
const float colSpeed = .2;
const float exponant = 7.;
//Set mouseControl to true to enable mouse control
const bool mouseControl = false;

vec3 palette(float t)
{
    vec3 a = vec3(.5,.5,.5);
    vec3 b = vec3(.5,.5,.5);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(.0,0.33333,0.66666);

    return a+b*cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    vec2 mouse = (iMouse.xy/iResolution.xy)-.5;
    float t = iTime;

    if(mouseControl) uv = abs(uv-mouse*.5);
    else uv = abs(uv);

    float m = exp(length(uv)*exponant);
    m = sin(m*nb + t*speed)/nb;
    m = abs(m);
    //m = step(border,m);  
    m = smoothstep(0.02, 0.05, m);

    vec3 finalCol = vec3(m * palette(length(uv)+t*colSpeed));
    fragColor = vec4(finalCol,1.0);
}
