float lorenzian(float x, float Q){
    Q = 1./(Q*Q);
    return Q/(Q+x*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float gd = length(uv);
    vec3 finCol = vec3(0.0);

    vec3 col = vec3(1.0, 2.0, gd);

    for (float i = 0.0; i < 3.0; i++) {
        float speed = 3.0 - i;
        float amp = lorenzian(uv.x,1.);
        float freq = 4.0;

        float d = amp * sin((uv.x + iTime * speed) * freq) - uv.y;
        d = lorenzian(d,100.);

        finCol += d * col;
    }

    fragColor = vec4(finCol, 1.0);
}
