float N21(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float fBm(vec2 p) {
    float value = 0.0;
    float amplitude = 1.5;
    for (int i = 0; i < 1; i++) {
        value += amplitude * N21(floor(2.0*p));
        p *= 2.0;
        amplitude *= 0.5;
    }
    return value;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float y = 0.5 + 0.2 * fBm(vec2(uv.x + iTime * 0.1, 0.0));
    vec3 col = vec3(0.0);
    if (uv.y < y) {
        col = vec3(0.0, 0.0, 0.3); // water color
    } else {
        col = vec3(0.0, 0.6, 0.0); // land color
    }
    fragColor = vec4(col, 1.0);
}
