#define MAX_PARTICLES 50.
#define MAX_circles 10.


float Firework(vec2 uv, vec2 pos){
    float brigtness = 0.00005;
    float d = length(uv-pos);
    float c = brigtness/d;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy)-.5;

    uv.y *= iResolution.y/iResolution.x;
    float t = iTime;
    float frt = fract(t);
    float flt = floor(t);
    vec3 color = vec3(.0);

    for(float j = 1.; j <= MAX_circles; j++){
        float n = 1.0+j/abs(sin(t));
        for(float i = 1.; i <= MAX_PARTICLES; i++){
            float particle = Firework(uv, vec2(sin((t)+i)/n, cos((t*2.)+i)/n/2.));

            color += particle*vec3(abs(sin(t))+.2, .0, abs(cos(t))+.3);
        }
    }

    // Output to screen
    fragColor = vec4(color,1.0);
}
