#define time 1. * iTime

vec2 complex_exp(vec2 z)
{
    return vec2(exp(z.x) * cos(z.y), exp(z.x) * sin(z.y));
}

float noise_mouse(vec2 uv)
{
    float r   = 80.9 * smoothstep(180., 0., time);
    float arg = .1;
    vec2  cxy = vec2(r, arg);
    cxy = complex_exp(cxy);

    float r_ = pow(uv.x, 2.) + pow(uv.y, 2.);

    return tan(cxy.x * length(uv) * .1);
}

float rest_noise(vec2 uv)
{
    uv = uv/iResolution.x;

    uv.x -= .5;
    uv.y -= .5 * iResolution.y / iResolution.x;

    //float alpha = length(vec2(iMouse.x-400., iMouse.y-225.) * .02);

    float r = length(uv);
    float t = atan(uv.y, uv.x);
    uv.x = r*sin(8.*t + tan(floor(0.1*time * log(time))));
    uv.y = r*cos(8.*t + tan(floor(0.1*time * log(time))));

    float a  = noise_mouse(uv);
    float b = 0., c = 0.;
    b += noise_mouse(uv + vec2( uv.x * .1, uv.y * .0));
    b += noise_mouse(uv + vec2( uv.x * .0, uv.y * .1));
    b += noise_mouse(uv + vec2( uv.x * .0, uv.y *-.1));
    b += noise_mouse(uv + vec2( uv.x *-.1, uv.y * .0));

    float col = 0.6*a + 0.1*b;
    return clamp(col, 0., 1.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col = rest_noise(fragCoord);
    float neis = 0.;
    neis = rest_noise(fragCoord + vec2( 1., 0.));
    neis += rest_noise(fragCoord + vec2(-1., 0.));
    neis += rest_noise(fragCoord + vec2( 0., 1.));
    neis += rest_noise(fragCoord + vec2( 0.,-1.));
    neis *= .25;

    col = mix(col, neis, 0.25);

    fragColor = vec4(vec3(col), 1.0);
}
