#define PI 3.141592653
#define COUNT 20.0

float line(vec2 st, float width)
{
    return smoothstep(width, 0.0, abs(st.y - st.x));
}

float drawCoord(vec2 uv)
{
   float x = distance(vec2(uv.x, 0.0), vec2(0.0, 0.0));
   float y = distance(vec2(0.0, uv.y), vec2(0.0, 0.0));

   return 1e-3 / x + 1e-3 / y;
}

float sphere(vec2 coord, vec2 p, float radius)
{
    float d = distance(p, coord);
    d = abs(d - radius);
    return 1e-3 / d;
}

float sphereShape(vec2 coord, vec2 p, float radius)
{
    float d = distance(p, coord);
    d /= radius;
    d = 1.0 - d;
    d = clamp(d * 24.0, 0.0, 1.0);
    return d;
}

float net(vec2 coord)
{
    float xline = cos(coord.x * PI * COUNT);
    xline = step(1.0 - 1e-1, xline);

    float yline = cos(coord.y * PI * COUNT);
    yline = step(1.0 - 1e-1, yline);

    return xline + yline;
}

float QuadrantIV(vec2 coord, float warp)
{
    float f = step(0.0, -coord.x);
    f *= step(0.0, coord.y);
    return abs(warp - f);
}

float QuadrantII(vec2 coord, float warp)
{
    float f = step(0.0, coord.x);
    f *= step(0.0, -coord.y);
    return abs(warp - f);
}

float noise(float x)
{
    return fract(sin(x * 43.3459 + 248.2435) * 545.344898);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float asp = iResolution.x / iResolution.y;

    vec2 coord = uv * 2.0 - 1.0;

    vec2 aspCoord = vec2(coord.x * asp, coord.y);

    vec2 spCoord = aspCoord;

    vec3 col = vec3(0.0);

    col += net(aspCoord) * 0.125;

    col += drawCoord(coord);
    aspCoord.x += fract(iTime * 0.01) * 20.0 * PI;
    float x = sin(aspCoord.x * PI * 4.0);
    x += sin(aspCoord.x * 5.2995);
    x += sin(aspCoord.x * 3.1245) * 0.5;
    x += sin(aspCoord.x * 2.1245) * 0.66;
    x += sin(aspCoord.x * 18.1245) * 0.42;

    col += line(vec2(x, aspCoord.y * 4.0), 0.09);

    col += sphereShape(spCoord * 4.0, vec2(-0.1, x + 0.3), 0.15);
    col += sphereShape(spCoord * 4.0, vec2(0.0, x + 0.3), 0.3);
    col += sphereShape(spCoord * 4.0, vec2(0.1, x + 0.3), 0.6);

    // Output to screen
    fragColor = vec4(col,1.0);
}
