// https://www.shadertoy.com/view/DlcyWs alternating domino tiling, 2023 by jt

// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2,
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
int triple32(int x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bb;
    x ^= x >> 11;
    x *= 0xac4c1b51;
    x ^= x >> 15;
    x *= 0x31848bab;
    x ^= x >> 14;
    return x;
}

#define HASH(u) triple32(u)

int ihash(ivec2 v)
{
    return HASH(v.x + HASH(v.y));
}

bool bhash(ivec2 v)
{
    return (ihash(v) & 1) != 0;
}

ivec2 split(ivec2 tile, ivec2 quadrant, bool flip)
{
    // NOTE: double square index to avoid overlapping indices for dominos
    //       (would be exact for one dimensional index,
    //        in the case of the two-dimensional index
    //        some (a quarter?) indices will be unused)
    return 2*tile + (flip ? ivec2(quadrant.x, 0) : ivec2(0, quadrant.y));
}

// random domino tiling, returns unique tile index (but some indices unused)
ivec2 random_domino_tiling(ivec2 position) // https://www.shadertoy.com/view/mtyyDR random domino tiling int (jt)
{
    ivec2 quadrant = position & 1; // [0,0] to [1,1]
    ivec2 tile = position - quadrant; // round down to even number
    bool flip = bhash(tile);

    if(flip)
    {
        if(bhash(tile + 2 * ivec2(quadrant.x * 2 - 1, 0))) // neighbor tile aligned?
        {
            tile += ivec2(quadrant.x * 2 - 1, 0); // adjust index to merged tile
            quadrant.x ^= 1; // horizontal merge: flip horizontal quadrant
            return split(tile, quadrant, bhash(tile));
        }
    }
    else
    {
        if(!bhash(tile + 2 * ivec2(0, quadrant.y * 2 - 1))) // neighbor tile aligned?
        {
            tile += ivec2(0, quadrant.y * 2 - 1); // adjust index to merged tile
            quadrant.y ^= 1; // vertical merge: flip vertical quadrant
            return split(tile, quadrant, bhash(tile));
        }
    }

    return split(tile, quadrant, flip);
}

bool edge(vec2 I)
{
    float e = 0.1;
    ivec2 a = random_domino_tiling(ivec2(floor(I-e))).xy;
    ivec2 b = random_domino_tiling(ivec2(floor(I+e))).xy;
    return any(not(equal(a, b)));
}

vec3 hash32(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

void mainImage(out vec4 o, in vec2 I)
{
    vec2 R = iResolution.xy;
    I = (2. * I - R) / R.y;
    vec3 color = vec3(0);
    if(all(lessThan(abs(I), vec2(1))))
    {
        I *= 10.0;
        ivec2 position = ivec2(floor(I));
        ivec2 index = random_domino_tiling(position);
        color = mix(vec3(1), hash32(vec2(index)), 0.75);
        if(edge(I)) color *= 0.0;
        if(iMouse.z > 0.0)
        {
            vec2 grid = 1.0 - smoothstep(0.0, 50.0/R.y, abs(fract(I+0.5)-0.5));
            color = max(color, max(grid.x, grid.y));
        }
    }  
    o = vec4(color, 1);
}
