// taken from https://gist.github.com/ayamflow/c06bc0c8a64f985dd431bd0ac5b557cd
vec2 rotateUV(vec2 uv, float rotation, vec2 mid)
{
    return vec2(
      cos(rotation) * (uv.x - mid.x) + sin(rotation) * (uv.y - mid.y) + mid.x,
      cos(rotation) * (uv.y - mid.y) - sin(rotation) * (uv.x - mid.x) + mid.y
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 centerCoord = vec2(0.5) * iResolution.xy;

    float dist = max(0.0, iResolution.y - distance(fragCoord, centerCoord));

    float rotation = round(dist * 0.02);

    float strength = distance(centerCoord, iMouse.xy) * (1.0 / iResolution.x);

    vec2 ruv = rotateUV(uv, rotation * strength, vec2(0.5,0.5));

    fragColor = texture(iChannel0, ruv);
}
